import React, { useEffect, useState } from 'react'

export const CartContext = React.createContext();

export function CartProvider(props) {

    const [cartItems, setCartItems] = useState([]);
    const [clickedCart, setClickedCart] = useState(0);

    const addToCart = (product) => {
        setClickedCart(clickedCart + 1) // scroll on click to cart

        // setCartItems(cartItems=> [...cartItems, () => {
        //     for (let i of cartItems) {
        //         if (i._id === product._id) {
        //             i.count += 1 
        //         } else {
        //             cartItems.push({
        //                 id: product._id,
        //                 count: 1
        //             })
        //         }
        //     }
        // }])
        setCartItems(cartItems=>[...cartItems, product._id])
        test(product._id)
    }
    const test = (productId) => {
        for (let i of cartItems) {
            if (i._id === productId) {
                i.count += 1 
            } else {
                cartItems.push({
                    id: productId,
                    count: 1
                })
            }
        }
    }

    return (
        <CartContext.Provider
            value={{
                cartItems: cartItems,
                addToCart: addToCart,
                clickedCart: clickedCart
            }}
        >
            {props.children}
        </CartContext.Provider>
    )
}
