import React, { useContext } from 'react';
import '../../App.css';
import '../../Styles/Account.css'
import {
    withRouter
} from 'react-router-dom'
import { UserContext } from '../../contexts/User'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCommentDots } from '@fortawesome/free-solid-svg-icons';

function AccountInfo(props) {

    const { 
        userInfo
    } = useContext(UserContext);

    console.log(userInfo)

    return(
        <div className='AccountInfo'>
            <div className="accountinfo-container">
                <div className="accountinfo-menu">
                    <div className="accountinfo-avt flex">
                        <img 
                            className="accountinfo-avt-img"
                            src={userInfo.userAvt || "https://cdn.onlinewebfonts.com/svg/img_258083.png"} 
                            alt=""
                            width="48px"
                            height="48px"
                        ></img>
                        <div className="accountinfo-avt-name">
                            {userInfo.userName}
                        </div>
                    </div>
                    <div className="accountinfo-menu-list">
                        <div className="accountinfo-menu-item flex">My account</div>
                        <div className="accountinfo-menu-item flex">Orders</div>
                        <div className="accountinfo-menu-item flex">Notification</div>
                        <div className="accountinfo-menu-item flex">Log out</div>
                    </div>
                </div>
                <div className="accountinfo-main"></div>
            </div>
        </div>
    )
}

export default withRouter(AccountInfo);
