import React from 'react';
import '../../App.css';
import DropdownSlider from './DropdownSlider.js'

import womenImg from '../../assets/women-dropdown.jpg'
import womenImg2 from '../../assets/women-dropdown2.jpg'
import womenImg3 from '../../assets/women-dropdown3.jpg'
import ScrollAnimation from 'react-animate-on-scroll';

export default function Dropdown(props) {
    return(
        <div className={props.scrolled === false ? "Dropdown" : "Dropdown dropdown-display"}>
            <div className="dropdown-container flex">
                { props.dropdownContent.map((item, index) => {
                    return (
                        <div 
                            className="dropdown-col flex"
                            key={index}
                        >
                            <div>
                                <ScrollAnimation 
                                    animateIn='fadeInRight' 
                                    animateOut='fadeInLeft' 
                                    className="dropdown-title">{item.dropdownTitle}
                                </ScrollAnimation>
                                <ScrollAnimation 
                                    animateIn='fadeInLeft' 
                                    className="dropdown-item flex-col">
                                    {
                                        item.dropdownList.map((item, index) => {
                                            return (
                                                <div
                                                    key={index}
                                                >
                                                    {item}
                                                </div>
                                            )
                                        })
                                    }
                                </ScrollAnimation>
                            </div>
                        </div>
                    )
                })}
                { props.label === 'Women' &&
                    <DropdownSlider 
                        width={"450"} 
                        height={"300"}
                        imgs={[
                            womenImg,
                            womenImg2,
                            womenImg3,
                        ]}
                    ></DropdownSlider>
                }
                { props.label === 'Men' &&
                    <DropdownSlider 
                        id={props.id}
                        width={"450"} 
                        height={"300"}
                        imgs={[
                            womenImg,
                            womenImg,
                            womenImg,
                        ]}
                    ></DropdownSlider>
                }
            </div>
        </div>
    )
}
