import React, { useEffect, useState } from 'react'
import '../../../../App.css'
import '../../../../Styles/Dashboard.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faGlobe } from '@fortawesome/free-solid-svg-icons'  
// import { Bar } from '@reactchartjs/react-chart.js'
import { Line } from '@reactchartjs/react-chart.js'

export default function DashboardChart(props) {

    const products = props.products 
    const order = props.order 
    const [top3Cate, setTop3Cate] = useState([])
    const [data, setData] = useState({})

    useEffect(()=>{
        if (products.length > 0 && order.length > 0) {
            const allCate = Object.values(products.reduce((a, {productCate}) => {
                a[productCate] = a[productCate] || {productCate, count: 0};
                return a;
            }, Object.create(null)));

            for (let i in allCate) {
                for (let j in products) {
                    if (allCate[i].productCate === products[j].productCate) { 
                        allCate[i].count += products[j].productSold
                    }
                }
            }
            allCate.sort((a, b) => b.count - a.count)
            const arr = allCate.splice(0,3); 
            console.log(arr)

            // setData({ 
            //     labels: ['Jan', 'Feb', 'Mar', 'Apr', 'Mai', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
            //     datasets: [
            //         {
            //             label: arr[0].productCate,
            //             data: [12, 19, 3, 5, 2, arr[0].count],
            //             backgroundColor: 'rgb(255, 99, 132)',
            //         },
            //         {
            //             label: arr[1].productCate,
            //             data: [2, 3, 20, 5, 1, arr[1].count],
            //             backgroundColor: 'rgb(54, 162, 235)',
            //         },
            //         {
            //             label: arr[2].productCate,
            //             data: [3, 10, 13, 15, 22, arr[2].count],
            //             backgroundColor: 'rgb(75, 192, 192)',
            //         },
            //     ],
            // })
            setTop3Cate(arr)
        }
    },[products, order]) 

    // const options = {
    //     scales: {
    //         yAxes: [
    //         {
    //             ticks: {
    //                 beginAtZero: true,
    //             },
    //         },
    //         ],
    //     },
    //     legend: {
    //         labels: { 
    //             fontSize: 14
    //         }
    //     }
    // }

    return (
        <div className="chart flex-col">
            <div className="headerbox flex-center">
                <FontAwesomeIcon icon={faGlobe} className="icon"/>
            </div>
            <div className="top-location-container">
                <div className="headerbox-header">
                    <p>2020 Sales by Top 3 Categories</p>
                </div>
                <div className="top-location-content flex">
                    <div className="top-location-map" style={{margin: '0'}}>
                        {/* <Bar data={data} options={options} />  */}
                        <Line data={data} options={options} /> 
                    </div>
                </div>
            </div>
        </div>
    )
}
