import React, { useContext, useEffect, useState } from 'react'
import '../../../App.css'
import '../../../Styles/Dashboard.css'
import DashboardBody from './DashboardBody'
import DashboardMenu from './DashboardMenu'
import { faEnvelope, faFileInvoice, faHome, faInbox, faNewspaper, faShoppingBag, faTshirt, faUser } from '@fortawesome/free-solid-svg-icons'

import socketIOClient from "socket.io-client" 
import Axios from 'axios'
import { withRouter } from 'react-router-dom'
const ENDPOINT = "http://pe.heromc.net:4000";

function Dashboard(props) {
    const menuItems = [
        {
            id: "1",
            name: "Dashboard",
            icon: faHome
        },
        {
            id: "2",
            name: "Live Chat",
            icon: faInbox
        },
        // {
        //     id: "3",
        //     name: "Email",
        //     icon: faEnvelope
        // },
        {
            id: "3",
            name: "Orders",
            icon: faFileInvoice
        },
        {
            id: "4",
            name: "Products",
            icon: faTshirt
        },
        {
            id: "5",
            name: "News",
            icon: faNewspaper
        },
        {
            id: "6",
            name: "Users",
            icon: faUser
        },
        {
            id: "7",
            name: "Collection",
            icon: faShoppingBag
        },
        {
            id: "8",
            name: "Subscribers",
            icon: faEnvelope
        },
    ]
    const [tabId, setTabId] = useState("1");
    const [openMenu, setOpenMenu] = useState(true);
    const [productId, setProductId] = useState("")

    const socket = socketIOClient(ENDPOINT);

    const [orderNotice, setOrderNotice] = useState(null) 
    const [isAdmin, setIsAdmin] = useState(true)

    useEffect(()=>{
        if (localStorage.getItem('token')) {
            Axios.get(`http://pe.heromc.net:4000/users/${localStorage.getItem('user-id')}`, { 
                headers: {"authorization" : `Bearer ${localStorage.getItem('token')}`}
            })
            .then(res => {
                const userInfo = res.data.user; 
                if (userInfo.userRole === 'admin') {
                    socket.emit('join', {
                        sessionId: 'admin',
                        isAdmin: true
                    })
                    socket.on("placeAnOrder-notice", function(data) {
                        setOrderNotice(data)
                    }) 
                } else {
                    props.history.push('/admin')
                    setIsAdmin(true)                
                }
            })
            .catch(err => { 
                console.log(err)
            })
        } else {
            props.history.push('/admin')
        }
    },[]) 

    const setTabIdOnClick = (id) => {
        setTabId(id);
    }
    const setOpenMenuOnClick = (id) => {
        if (openMenu) setOpenMenu(false);
        else setOpenMenu(true);
    }

    const [openCreate, setOpenCreate] = useState(false)

    const setOpenCreateFunc = () => {
        document.body.style.overflow = 'hidden';
        setOpenCreate(true)
    }

    const setCloseCreateFunc = (bool) => {
        document.body.style.overflow = 'unset';
        setOpenCreate(bool)
    }

    const [openEdit, setOpenEdit] = useState(false)

    const setOpenEditFunc = (event) => {
        document.body.style.overflow = 'hidden';
        setOpenEdit(true)
        setProductId(event.target.id)
    }

    const setCloseEditFunc = (bool) => {
        document.body.style.overflow = 'unset';
        setOpenEdit(bool)
    }

    return (
        <div>
            {
                notAdmin && 
                <div>
                    cut
                </div>
            }
            {
                notAdmin === false &&
                <div className="Dashboard flex">
                    <DashboardMenu
                        setTabIdOnClick = {setTabIdOnClick}
                        setOpenMenuOnClick = {setOpenMenuOnClick}
                        tabId = {tabId}
                        menuItems = {menuItems}
                        openMenu = {openMenu} 
                        setCloseCreateFunc={setCloseCreateFunc}
                        setCloseEditFunc={setCloseEditFunc}
                    />
                    <DashboardBody
                        tabId = {tabId}
                        menuItems = {menuItems}
                        openMenu = {openMenu}
                        openCreate = {openCreate}
                        openEdit = {openEdit}
                        setOpenMenuOnClick = {setOpenMenuOnClick}
                        setOpenCreateFunc={setOpenCreateFunc}
                        setCloseCreateFunc={setCloseCreateFunc}
                        setOpenEditFunc={setOpenEditFunc}
                        setCloseEditFunc={setCloseEditFunc}
                        productId={productId}
                        orderNotice={orderNotice}
                    />
                </div>
            }
        </div>
    )
}
export default withRouter(Dashboard)
