import React, { useEffect, useState } from 'react'
import classNames from 'classnames'
import {
    Link, withRouter
  } from "react-router-dom"; 
import Dropdown from './Dropdown';

function MenuItemDropdown(props) {

    const dropdownHover = props.dropdownHover;
    const location = props.location.pathname;
    const [productGroupCate, setProductGroupCateList]= useState([])

    useEffect(()=>{
        axios.get(`http://pe.heromc.net:4000/products`)
            .then(res => {
                const test = Object.values(res.data.reduce((a, {productGroupCate}) => {
                    a[productGroupCate] = a[productGroupCate] || {productGroupCate};
                    return a;
                }, Object.create(null)));
                setProductGroupCateList(test)
            }
        )
    },[])
    return (
        <li  
            className="menu-item"
            onClick={props.handleClick}
            onMouseEnter={props.handleHover}
            onMouseLeave={props.handleLeaveHover}
            >
            <Link to={props.url}
                className={classNames({
                    active: location === props.url,
                    whitelink_header: props.whiteText === true,
                })}
            >{props.label}</Link>
            {(dropdownHover === true && props.dropdownContent.length > 0) && 
                <Dropdown 
                    className="dropdown-display"
                    dropdownContent={props.dropdownContent}
                    label={props.label}
                    scrolled={props.scrolled}
                /> 
            }
        </li>
    )
}
export default withRouter(MenuItemDropdown);
