import React, { useEffect, useRef, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'

export default function DashboardOrderCreate(props) {

    const createForm = useRef();

    const [orderName, setOrderName] = useState("")
    const [orderEmail, setOrderEmail] = useState("")
    const [orderPhone, setOrderPhone] = useState("")
    const [orderAddress, setOrderAddress] = useState("")
    const [orderProvince, setOrderProvince] = useState("")
    const [orderDistric, setOrderDistric] = useState("")
    const [orderPaymentMethod, setOrderPaymentMethod] = useState("")


    const onSubmit = (event) => {
        event.preventDefault()
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        const formData = new FormData();

        formData.append("userName", userName);

        axios.post('http://localhost:4000/users/register', formData, config)
        props.setCloseCreateFunc(false);
        props.setToastFunc(true);
    }

    return (
        <div className="DashboardProductInfo">
            <div className="create-box"> 
                <div className="create-box-title flex">
                    <div className="create-box-title-text">
                        Order infomation
                    </div>
                    <div  
                        className="create-box-title-close flex-center"
                        onClick={()=>{
                            props.setCloseCreateFunc(false);
                        }}
                    >
                        <FontAwesomeIcon icon={faTimes}/>
                    </div>
                </div>
                <form onSubmit={onSubmit} encType="multipart/form-data" ref={createForm}>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Name</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="name" 
                                value={orderName || ""}
                                onChange={(event)=>{
                                    setOrderName(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Email</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="email" 
                                value={orderEmail || ""}
                                onChange={(event)=>{
                                    setOrderEmail(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Phone</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="phone" 
                                value={orderPhone || ""}
                                onChange={(event)=>{
                                    setOrderPhone(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Province</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="phone" 
                                value={orderProvince || ""}
                                onChange={(event)=>{
                                    setOrderProvince(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">District</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="phone" 
                                value={orderDistric || ""}
                                onChange={(event)=>{
                                    setOrderDistric(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Address</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="phone" 
                                value={orderAddress || ""}
                                onChange={(event)=>{
                                    setOrderAddress(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Items</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="phone" 
                                value={userEmail || ""}
                                onChange={(event)=>{
                                    setUserEmail(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Payment method</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="phone" 
                                value={userEmail || ""}
                                onChange={(event)=>{
                                    setUserEmail(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="flex-center" style={{marginTop: '40px'}}>
                        <button className="create-box-btn btn">
                            Create order
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
}
