import React, { Component, useState } from 'react';
import '../App.css';

export default function ProductReview(props) {
    const [tabId, setTabId] = useState(0);
    return(
        <div className="ProductReview">
            <div className="productreview-container">
                <div className="productreview-tab flex-center">
                    <div 
                        className="productreview-title search-tab login-tab"
                        onClick={()=>{setTabId(0)}}>
                        Description
                    </div>
                    <div className="productreview-title"
                        onClick={()=>{setTabId(1)}}>
                        Reviews
                    </div>
                </div>
                <div className="productreview-content">
                    {
                        tabId === 0 && 
                        <div> 
                            {props.productDes}
                        </div>
                    }
                    {
                        tabId === 1 && 
                        <div> 
                            {props.productVote[0].ratingStar}
                        </div>
                    }
                </div>

                <div 
                    className='search-tab login-tab flex'>
                    <div 
                        className={tabID === 0 ? 'search-tab-cate search-tab-active' : 'search-tab-cate'}
                        onClick={() => setTabID(0)}
                        >
                        Login
                    </div>
                    <div 
                        className={tabID === 1 ? 'search-tab-cate search-tab-active' : 'search-tab-cate'}
                        onClick={() => setTabID(1)}
                        >
                        Register
                    </div>
                </div>

                { tabID === 0 &&
                    <div className="search-form login-form fadeToRight">
                        <form className="flex-col">
                            <input placeholder="Username"/>
                            <input placeholder="Password"/>
                            <div className="remember-login flex noselect" 
                                onClick={() => { 
                                    if (check) {
                                        setCheck(false)
                                    } else { 
                                        setCheck(true) 
                                    }
                                }}
                            >
                                <div className="check-box"></div>
                                {check && 
                                    <div className="check-box-active flex-center" onClick={()=> setCheck(false)}>
                                        <FontAwesomeIcon className="check-box-active" icon={faCheck}></FontAwesomeIcon>
                                    </div>
                                }
                                <p>Remember me</p>
                            </div>
                            <button>LOGIN</button>
                            <label>LOST YOUR PASSWORD?</label>
                        </form>
                    </div>
                }
                { tabID === 1 && 
                    <div className="search-form login-form fadeToLeft">
                        <form className="flex-col">
                            <input placeholder="Username"/>
                            <input placeholder="Password"/>
                            <button>REGISTER</button>
                        </form>
                    </div>
                }
            </div>
        </div>
    )
}
