import React, { useEffect, useState } from 'react'

export const CartContext = React.createContext();

export function CartProvider(props) {

    const [cartItems, setCartItems] = useState([]);
    const [clickedCart, setClickedCart] = useState(0);

    useState(()=>{

    },[cartItems])

    const addToCart = (product) => {
        setClickedCart(clickedCart + 1) // scroll on click to cart

        setCartItems(cartItems => [...cartItems, product])

        const virtualCart = cartItems;
        if (cartItems.length === 0) {
            virtualCart = virtualCart.concat({...product, quantity: 1});
        }  else {
            if (!this.isExists(virtualCart, item)) {
                virtualCart = virtualCart.concat({...item, quantity: 1});
            } else {
                virtualCart = cartItems.map(value => {
                if (value._id === item._id) {
                    return { ...value, quantity: value.quantity + 1};
                } else {
                    return { ...value };
                }
                })
            }
        }
    }

    const updateOnRemoveCart = (product) => {
        setCartItems(product)
    }

    const updateOnMinusCart = (product) => {
        console.log(product)
    }

    return (
        <CartContext.Provider
            value={{
                cartItems: cartItems,
                addToCart: addToCart,
                clickedCart: clickedCart,
                updateOnRemoveCart: updateOnRemoveCart,
                updateOnMinusCart: updateOnMinusCart
            }}
        >
            {props.children}
        </CartContext.Provider>
    )
}
