import React, { useEffect, useState } from 'react';
import '../../App.css';
import Product from '../Product.js';
import axios from 'axios'

export default function SalesProducts(props) {

    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:4000/products`)
            .then(res => {
                setProducts(res.data)
            }
        )
    },[])

    console.log(products)
    //Get product selling
    const sellingProduct = [];
    for (let i = 0; i < products.length; i++) {
        console.log(products[i].productSale)
        // if (Number(i.productSale) > 0) {
        //     console.log(i)
        //     sellingProduct.push(i.productSale);
        // }
    }

    console.log(sellingProduct)

    return(
        <div className="SalesProducts">
            {products.map(function(item, index) {
                return (
                    <Product 
                        key={index}
                        imgUrl={item.imgUrl} 
                        imgUrlHover={item.imgUrlHover} 
                        productTitle={item.productTitle}
                        productPrice={item.productPrice}
                        productSale={item.productSale}
                        productSold={item.productSold}
                    />
                )
            })}
        </div>
    )
}
