import React, { Component } from 'react';
import '../App.css';

export default class NewsBody extends Component {

    render() {
        const { news } = this.props;

        const firstPost = news.splice(0,1);
        const nextPosts = news.slice(1);

        return(
            <div className="NewsBody">
                <div className="newsbody-container">
                    <div className="newsbody-post">
                        {firstPost.map((item, index) => {
                            return(
                                <div className="newsbody-big" key={index}>
                                    <img className="newsbody-big-img" src={item.newImg} alt="z"/>
                                    <div className="newsbody-big-time">{item.newTime}</div>
                                    <div className="newsbody-big-title">{item.newTitle}</div>
                                    <div className="newsbody-big-content">{item.newContent}</div>
                                    <div className="newsbody-big-link">Read More</div>
                                    <div className="newsbody-big-content">{item.id}</div>
                                </div>
                            )
                        })}
                        <div className="newsbody-small"> 
                        {nextPosts.map((item, index) => {
                            return(
                                <div className="newsbody-big" key={index}>
                                    <img className="newsbody-big-img" src={item.newImg} alt="z"/>
                                    <div className="newsbody-big-time">{item.newTime}</div>
                                    <div className="newsbody-big-title">{item.newTitle}</div>
                                    <div className="newsbody-big-content">{item.newContent}</div>
                                    <div className="newsbody-big-link">Read More</div>
                                </div>
                            )
                        })}
                        </div>
                    </div>
                    <div className="newsbody-nav">
                        <div className="newsbody-search">search</div>
                        <div className="newsbody-pop">popular</div>
                        <div className="newsbody-cate">category</div>
                        <div className="newsbody-tag">TAg</div>
                    </div>
                </div>
            </div>
        )
    }
}
