import React from 'react'
import { useEffect, useState } from 'react'
import socketIOClient from "socket.io-client";

const ENDPOINT = "http://localhost:4000";

export default function DashboardInbox(props) {
    const [allChatData, setAllChatData] = useState([])

    const socket = socketIOClient(ENDPOINT);

    useEffect(() => {
        socket.on('connect', function (data) {
            socket.emit('admin-join')
        })
        socket.on('send-all-chat', (data)=>{
            setAllChatData(data)
        })
        // socket.on('messageSend-thread', (data) => {
        //     alert(data)
        // })
        // socket.on("client-msg", function(data) {
        //     console.log(data)
        // })
    },[])

    const chooseRoom = (event) => {
        console.log(event.target.value)
        socket.emit('sendRoomId', )
    }

    console.log(allChatData)

    return (
        allChatData.map((item, index)=> {
            return (
                <div 
                    key={index} className="flex"
                    onClick={chooseRoom}
                    value={item.sessionId}
                >
                    <p>{item.chatName}</p>
                    <p>{item.chatEmail}</p>
                    {item.chatContent.map((item,index)=>{
                        return (
                            <div key={index}>
                                <p>{item.text}</p>
                                <p>{item.date}</p>
                            </div>
                        )
                    })}
                </div>
            )
        })
    )
}
