import React from 'react';
import '../../App.css';

export default function Dropdown(props) {

    console.log(props.dropdownContent[0].dropdownList)
    const dropdownList = props.dropdownContent[0].dropdownList;

    return(
        <div className="Dropdown">
            <div className="dropdown-container flex">
                { props.dropdownContent.map((item, index) => {
                    return (
                        <div>
                            {/* {item} */}
                        </div>
                    )
                })}
                <div className="dropdown-item flex-col">
                    <div className="dropdown-title">{props.dropdownContent[0].dropdownTitle}</div>
                    {
                        dropdownList.map((item, index) => {
                            return (
                                <div
                                    key={index}
                                >
                                    item
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        </div>
    )
}
