import React, { useEffect, useState } from 'react';
import '../../App.css';
import dropDownImg from '../../assets/women-dropdown.jpg'
import dropDownImg2 from '../../assets/women-dropdown2.jpg'

export default function Dropdown(props) {

    const [imgIndex, setImgIndex] = useState(0);
    const imgs = [
        dropDownImg,
        dropDownImg2,
    ]

    useEffect(()=>{
        setInterval(() => {
            setImgIndex(imgIndex + 1)
        }, 1000);
    })

    if (imgIndex > imgs.length) { //infinity slider loop
        // setProductImgBig(productImgBig.concat(props.productImg))
        setImgIndex(0);
    }

    return(
        <div className="DropdownSlider">
            <div className="dropdownslider-container flex" style={{width: `${props.width}px`, height: `${props.height}px` }}>
                {
                    imgs.map((item, index) => {
                        return (
                            <img 
                                key={index}
                                style={{transform: `translateX(-${props.width * imgIndex}px`}}
                                src={item} width='100%' height='100%'></img>
                        )
                    })
                }
            </div>
        </div>
    )
}
