import React, { useState } from 'react'

export const UserContext = React.createContext();

export function UserProvider(props) {

    const [userInfo, setUserInfo] = useState([]);

    const setUserInfoFunc = (user) => {
        setUserInfo(user)
    }

    return (
        <UserContext.Provider
            value={{
                userInfo: userInfo,
            }}
        >
            {props.children}
        </UserContext.Provider>
    )
}
