import React, { Component } from 'react';
import '../App.css';
import {
    Link
  } from "react-router-dom"; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import classNames from 'classnames';
import {  faSearch, faUser, faCartPlus } from "@fortawesome/free-solid-svg-icons";

class Header extends Component {
    constructor(props) {
        super(props);

        this.state = {
            scrolled: false,
            isWhite: false,
            activeId: 1
        }
        this.handleClickEvent = this.handleClickEvent.bind(this);
    }

    componentDidMount() {
        window.addEventListener('scroll', e => this.handleNavigation(e));
    }

    handleNavigation = (e) => {
        const window = e.currentTarget;

        const isTop = window.scrollY < 100;
        if (window.scrollY  === 0) {
            this.setState({
                isWhite: false
            })
        }

        if (isTop !== true) {
            this.setState({
                scrolled: true,
                isWhite: true
            })
        } else {
            this.setState({
                scrolled: false
            })
        }

        if (this.prev > window.scrollY) {
            this.setState({
                scrolled: false
            })
        }
        this.prev = window.scrollY;
    };



    // componentWillUnmount() {
    //     window.removeEventListener('scroll');
    // }

    handleClickEvent(event) {
        event.preventDefault();
        let history = useHistory();
        this.setState({
            activeId: Number(event.target.id)
        })
    }

    render() {
        console.log("state" ,this.state.activeId);
        return(
            <div className={classNames('Header', {
                scrolled: this.state.scrolled === true,
                white: this.state.isWhite === true
                })}>
                <ul className="menu flex-center">
                    <li>
                        <Link 
                            to="/" 
                            className={classNames({
                                active: this.state.activeId === 1
                            })}
                            id="1" 
                            onClick={this.handleClickEvent}
                            >home</Link>
                    </li>
                    <li>
                        <Link to="/news"
                            className={classNames({
                                active: this.state.activeId === 2
                            })}
                            id="2" 
                            onClick={this.handleClickEvent} 
                            >News</Link>
                    </li>
                    <li>
                        <Link to="/" 
                            className={classNames({
                                active: this.state.activeId === 3
                            })}
                            id="3"
                            onClick={this.handleClickEvent} 
                            >men</Link>
                    </li>
                    <li>
                        <Link to="/" 
                            className={classNames({
                                active: this.state.activeId === 4
                            })}
                            id="4"
                            onClick={this.handleClickEvent} 
                            >women</Link>
                    </li>
                    <li>
                        <Link to="/" 
                            className={classNames({
                                active: this.state.activeId === 5
                            })}
                            id="5"
                            onClick={this.handleClickEvent} 
                            >children</Link>
                    </li>
                </ul>
                <div className="logo flex-center">
                    <Link to="/">
                        <img src="https://demo.uix.store/sober/wp-content/themes/sober/images/logo.svg" alt="logo"></img>
                    </Link>
                </div>
                <div className="cart flex-center"> 
                    <FontAwesomeIcon icon={faSearch} className="icon"/>
                    <FontAwesomeIcon icon={faUser} className="icon"/>
                    <div className="icon flex-center">
                        <FontAwesomeIcon icon={faCartPlus} className="cart-icon"/>
                        <div className="cart-count">
                            <p>0</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Header;
