import React, { useRef } from 'react'
import '../../../../App.css'
import '../../../../Styles/Dashboard.css'
import { useEffect, useState } from 'react'


export default function DashboardEmail(props) {


    useEffect(() => {
        console.log(props.email)
    },[])

    return (
        <div className="boxchat-admin flex">
            <div className="boxchat-main">
                <div className="boxchat-box">
                    <div 
                        className="boxchat-contents"
                    >
                        { props.email.length > 0 &&
                            <div 
                                className="flex-col chat-box-list">
                                {
                                    props.email.map((item, index) => {
                                        return (
                                            <div 
                                                key={index}
                                                className="chat-list">
                                                    {item}
                                            </div>
                                        )
                                    })
                                }
                            </div>  
                        }
                    </div>
                </div>
                <div className="boxchat-type">
                    <form onSubmit={sendChatInput} className="boxchat-type-form">
                        <input 
                            type="text" 
                            onChange={handleOnChange}
                            value={chatInput}
                            placeholder="Type your message..."
                        ></input>
                        <button>Send</button>
                    </form>
                </div>
            </div>
        </div>
    )
}
