import React, { Component, useEffect, useState } from "react";
import "../App.css";
import Newsletter from "../components/Layouts/Newsletter.js"
import Footer from "../components/Layouts/Footer.js"
import BannerV3 from '../components/Banner/BannerV3.js'
import HeaderV3 from '../components/Header/HeaderV3.js'
import bg from '../assets/collection2.jpg'
import CollectionList from '../components/Collection/CollectionList.js'
import Axios from "axios";
import { withRouter } from "react-router-dom";

function Collection(props) {

    const [collection, setCollection] = useState({});
    const path = props.history.location.pathname.slice(12);

    useEffect(()=>{
        Axios.get(`http://localhost:4000/collection/${path}`)
            .then(res => {
                setCollection(res.data)
            }
        )
    },[])

    const collectionItems = []

    if(collection) {
        if (collection.collectionItems) {
            collection.collectionItems.map((item)=>{
                Axios.get(`http://localhost:4000/products/${item}`)
                    .then(res => {
                        collectionItems.push(res.data)
                        // console.log(res.data)
                    }
                )
            })
            console.log(collectionItems)
        }
    }


    return (
        <div className="Collection">
            <HeaderV3/>
            <BannerV3 
                bannerImage={collection.collectionBanner} collectionTitle={collection.collectionName}
            />
            <CollectionList
                collectionItems={collectionItems}
            />
            <Newsletter/>
            <Footer/>
        </div>
    )
}
export default withRouter(Collection)
