import React, { Component } from 'react';
import '../App.css';
import BestSeller from "../components/BestSeller.js"

export default class HomeTab extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentTab: 1
        }
    }

    render() {
        return(
            <div className="HomeTab">
                <div className="home-tab flex-center">
                    <p onClick={() => {this.setState({ currentTab: 1 })}} className="home-tav-active">Best Sellers</p>
                    <p onClick={() => {this.setState({ currentTab: 2 })}}>New Products</p>
                    <p onClick={() => {this.setState({ currentTab: 3 })}}>Sales Products</p>
                </div>
                <div className="tab-content">
                    {this.state.currentTab === 1 && <BestSeller/>}
                    {/* {this.state.currentTab === 2 && <NewProducts/>} */}
                    {/* {this.state.currentTab === 3 && <SalesProducts/>} */}
                </div>
            </div>
        )
    }
}
