import React, { useState } from 'react'
import '../Styles/Chat.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faComment } from '@fortawesome/free-solid-svg-icons'

export default function OpenChatBtn() {

    const [openChat, setOpenChat] = useState(false);
    const [onHover, setOnHover] = useState(false);

    return (
        <div 
            className="chat-btn"
            onClick={()=>{ if (openChat) { setOpenChat(false) } else setOpenChat(true) }}
            onMouseEnter={()=> { setOnHover(true) }}
            onMouseLeave={()=> { setOnHover(false) }}
        >
            <div className={onHover ? "chat-btn-container chat-btn-hover" : "chat-btn-container"}>
                <FontAwesomeIcon icon={faComment}/>
                <p>Live Chat</p>
                <div className={openChat ? "chat-box hide_chat_box" : "chat-box"}>
                    <div className="">
                    </div>
                </div>
            </div>
        </div>
    )
}
