import React, { useEffect, useState } from 'react';
import '../App.css';
import {
    Link,
    withRouter
  } from "react-router-dom"; 
import classNames from 'classnames';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {  faSearch, faUser, faCartPlus } from "@fortawesome/free-solid-svg-icons";
import Search from './Search';


function Header() {

    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         scrolled: false,
    //         isWhiteBox: false,
    //         activeId: 1,
    //         location: this.props.history.location.pathname,
    //         isWhiteText: false,
    //         isInvisBox: false,
    //         isSearchOpen: false
    //     }
    //     this.clickToClose = this.clickToClose.bind(this);
    //     this.clickToShow = this.clickToShow.bind(this);
    // }

    const [scrolled, setScrolled] = useState(false);
    const [whiteBox, setWhiteBox] = useState(false);
    // const [activeId, setActiveId] = useState(1);
    const [location, setLocation] = useState("");
    const [whiteText, setWhiteText] = useState(false);
    // const [invisBox, setInvisBox] = useState(false);
    const [searchOpen, setSearchOpen] = useState(false);


    function clickToShow() {
        this.setState({
            isSearchOpen: true
        }
    )}

    function clickToClose() {
        document.body.style.overflow = 'unset';
        setSearchOpen(false);
    }

    useEffect(() => {
        setLocation(this.props.history.location.pathname);
        window.scrollTo(0, 0);
    }, []);

    if(searchOpen){
        document.body.style.overflow = 'hidden';
    }

    return(
        <div 
            className={classNames('Header', {
                scrolled: scrolled === true,
                white: whiteBox === true
            })}
            onMouseEnter={() => { 
                if (location === "/news") {
                    setWhiteText(false); 
                }
            }}
            onMouseLeave={() => { 
                if (location === "/news") {
                    setWhiteText(true);
                }
            }}
            >
            <ul className="menu flex-center">
                <li>
                    <Link 
                        to="/" 
                        className={classNames({
                            active: location === "/",
                            whitelink_header: whiteText === true
                        })}
                        id="1"
                        >home</Link>
                </li>
                <li>
                    <Link to="/news"
                        className={classNames({
                            active: location === "/news",
                            whitelink_header: whiteText === true
                        })}
                        onClick={() => { this.setState({ whiteBox: false})}}
                        id="2"
                        >News</Link>
                </li>
                <li>
                    <Link to="/men" 
                        className={classNames({
                            active: location === "/men",
                            whitelink_header: whiteText === true
                        })}
                        id="3"
                        >men</Link>
                </li>
                <li>
                    <Link to="/women" 
                        className={classNames({
                            active: location === "/women",
                            whitelink_header: whiteText === true
                        })}
                        id="4"
                        >women</Link>
                </li>
                <li>
                    <Link to="/contact" 
                        className={classNames({
                            active: location === "/contact",
                            whitelink_header: whiteText === true
                        })}
                        id="5"
                        >contact</Link>
                </li>
            </ul>
            <div className="logo flex-center">
                <Link to="/">
                    {
                        whiteText === true 
                            ? <img src="https://demo.uix.store/sober/wp-content/themes/sober/images/logo-light.svg" alt="logo"></img>
                            : <img src="https://demo.uix.store/sober/wp-content/themes/sober/images/logo.svg" alt="logo"></img>
                    }
                </Link>
            </div>
            <div className={classNames('cart flex-center', {
                    whitelink_header: whiteText === true
                })}> 
                <FontAwesomeIcon 
                    icon={faSearch} 
                    className="icon"
                    onClick={()=> {
                        this.setState({ 
                            isSearchOpen: true 
                        })
                    }}
                    />
                <FontAwesomeIcon icon={faUser} className="icon"/>
                <div className="icon flex-center">
                    <FontAwesomeIcon icon={faCartPlus} className="cart-icon"/>
                    <div
                        className={classNames('cart-count', {
                            cart_count_news_hover: whiteText === true
                        })}> 

                        <p>0</p>
                    </div>
                </div>
            </div>
            <Search searchOpen={searchOpen} clickToClose={clickToClose()}/>
        </div>
    )
}
export default withRouter(Header);
