import React, { useEffect, useRef, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'
import DashboardEditor from './DashboardEditor';

export default function DashboardUserCreate(props) {

    const createForm = useRef();
    const [file, setFile] = useState([])

    const [userName, setUserName] = useState("")
    const [userPhone, setUserPhone] = useState("")
    const [userEmail, setUserEmail] = useState("")
    const [userImg, setUserImg] = useState([])

    const onSubmit = (event) => {
        event.preventDefault()
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }

        const formData = new FormData();

        const imageArr = Array.from(file);
        imageArr.forEach(image => {
            formData.append('userAvt', image);
        });
        formData.append("userName", userName);
        formData.append("userPhone", userPhone);
        formData.append("userEmail", userEmail);
        // axios.post('http://localhost:4000/news', formData, config)
        // props.setCloseCreateFunc(false);
        // props.setToastFunc(true);
    }

    const deleteImg = (event) => {
        const virutalFile = [...file]
        virutalFile.splice(event.target.id, 1)
        setFile(virutalFile)

        const items = [...userImg]
        items.splice(event.target.id, 1)
        setUserImg(items)
    }

    return (
        <div className="DashboardProductInfo">
            <div className="create-box"> 
                <div className="create-box-title flex">
                    <div className="create-box-title-text">
                        User infomation
                    </div>
                    <div  
                        className="create-box-title-close flex-center"
                        onClick={()=>{
                            props.setCloseCreateFunc(false);
                        }}
                    >
                        <FontAwesomeIcon icon={faTimes}/>
                    </div>
                </div>
                <form onSubmit={onSubmit} encType="multipart/form-data" ref={createForm}>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Name</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="name" 
                                value={userName || ""}
                                onChange={(event)=>{
                                    setUserName(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Avatar </div>
                        <div className="dashboard-right">
                            <input 
                                onChange={(event) => {
                                    const files = event.target.files;
                                    for (let i = 0; i< files.length; i++) {
                                        setUserImg(user=>[...user, URL.createObjectURL(files[i])])
                                    }
                                    const fileArr = Array.prototype.slice.call(files)
                                    fileArr.forEach(item=>{ 
                                        setFile(file=>[...file, item])
                                    })
                                }}
                                type="file"
                                name="userImg"
                                className="noborder"
                                multiple="multiple"
                                style={{height: '50px'}}
                            ></input>
                            <div className="flex" style={{ overflowY: 'hidden', flexWrap:'wrap'}}>
                                { userImg && 
                                    userImg.map((item, index) => {
                                        return (
                                            <div key={index} className="create-box-img">
                                                <img key={index} src={item} alt=""></img>
                                                <div 
                                                    className="create-box-img-overlay"
                                                >
                                                    <p
                                                        id={index}
                                                        onClick={deleteImg}
                                                        className="icon">X
                                                    </p>
                                                </div>
                                            </div>
                                        )
                                    })
                                }
                            </div>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Email</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="email" 
                                value={userEmail || ""}
                                onChange={(event)=>{
                                    setUserEmail(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Phone</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="phone" 
                                value={userPhone || ""}
                                onChange={(event)=>{
                                    setUserPhone(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Province</div>
                        <div className="dashboard-right">
                            <select 
                                className="input"
                                value={userTinh}
                                onChange={(event)=>{
                                    setProvinceId(event.target.selectedIndex)
                                    setUserTinh(event.target.value)
                                }}
                            >
                                <option disabled selected value>select a province</option>
                                {tinh.map((item, index) => {
                                    return (
                                        <option 
                                            key={index}
                                            value={item.name}
                                        >{item.name}</option>
                                    )
                                })}
                            </select>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">District</div>
                        <div className="dashboard-right">
                            <select 
                                className="input"
                                value={userHuyen}
                                onChange={(event)=>{
                                    setUserHuyen(event.target.value)
                                }}
                            >
                                <option disabled selected value>select a district</option>
                                {huyen.map((item, index) => {
                                    if (item.tinh_id === provinceId) {
                                        return (
                                            <option
                                                key={index}
                                                value={item.name}
                                                >{item.name}</option>
                                        )
                                    }
                                })}
                            </select>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Address</div>
                        <div className="dashboard-right">
                            <input 
                                type="text"
                                className="input"
                                name="phone" 
                                value={userAddress}
                                onChange={(event)=>{
                                    setUserAddress(event.target.value)
                                }}
                                value={userAddress}
                            ></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">New password</div>
                        <div className="dashboard-right">
                            <input 
                                type="password"
                                className="input"
                                name="email" 
                                value={userPassword}
                                onChange={(event)=>{
                                    setUserPassword(event.target.value)
                                }}
                                value={userPassword}
                            ></input>
                        </div>
                    </div>
                    <div className="flex-center" style={{marginTop: '40px'}}>
                    <button className="create-box-btn btn">
                        Edit product
                    </button>
                </div>
                </form>
            </div>
        </div>
    )
}
