import React, { Component } from 'react';
import '../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faTimes } from '@fortawesome/free-solid-svg-icons';

export default class Account extends Component {

    constructor(props) {
        super(props);
        this.inputElement = React.createRef();
    }

    componentDidUpdate() {
        this.inputElement.current.focus();
    }

    render() {
        return(
            <div className={this.props.accountOpen === false ? 'Search displayNone' : 'Search'}>
                <div className="search-header flex">
                    <div className="search-title">My Account</div>
                    <div
                        className="search-close"
                        onClick={this.props.clickToClose}
                        >
                        <FontAwesomeIcon 
                            icon={faTimes}
                            className="icon"
                            />
                    </div>
                </div >
                <div className={this.props.accountOpen === false ? '' : 'fadeIn'}>
                    <div 
                        className='search-tab flex'>
                        <div className="search-tab-cate search-tab-active">Login</div>
                        <div className="search-tab-cate">Register</div>
                    </div>
                    <div className="search-form">
                        <form>
                            <input placeholder="Search" ref={this.inputElement}/>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}
