import React, { useState } from 'react';
import '../../Styles/Product.css';
import '../../App.css';
import { faCartPlus, faHeart, faMinus, faPlus, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import ReactStars from 'react-rating-stars-component';
import { Link } from 'react-router-dom';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function ProductQuickView(props) {

    const [countCart, setCountCart] = useState(1);

    let ratingList = props.productVote.map(a => a.ratingStar); // get all rating
    const totalRating = ratingList.reduce((a, b) => a + b, 0)
    const averageRating = totalRating/ratingList.length;

    const ratingStar = {
        size: 12,
        value: averageRating,
        edit: false,
        activeColor: "#fda32a",
        color: "#ddd",
        isHalf: true
    };

    const settings = {
        infinite: true,
        speed: 50,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000
    };

    return(
        <div>
            <div className={props.view === true ? 'ProductQuickView' : 'ProductQuickView displayNone'}>
                <div 
                    className="productquickview-container flex"
                    onClick={()=>{}}
                >
                    <div
                        className="view-close flex-center"
                        onClick={props.closeView}
                        >
                        <FontAwesomeIcon 
                            icon={faTimes}
                            className="icon"
                            />
                    </div>
                    <div className="productquickview-slide">
                        <div className="productquickview-tag">
                            {
                                props.productSale > 0 && <div className="productquickview-tag-item sale">
                                    {props.productSale}%
                                </div>
                            }
                            {
                                props.productSold >= 40 && <div className="productquickview-tag-item hot">
                                    HOT
                                </div>
                            }
                            {
                                props.productSale > 0 && <div className="productquickview-tag-item new">
                                    NEW
                                </div>
                            }
                        </div>
                        <Slider {...settings}>
                            <img src={props.imgUrl} alt="" className="view-img"/>
                            <img src={props.imgUrl} alt="" className="view-img"/>
                            <img src={props.imgUrl} alt="" className="view-img"/>
                        </Slider>
                    </div>

