import React from 'react'
import '../../../App.css'
import '../../../Styles/Dashboard.css'
import { useEffect, useState } from 'react'
import socketIOClient from "socket.io-client";

const ENDPOINT = "http://localhost:4000";

export default function DashboardInbox(props) {
    const [allChatData, setAllChatData] = useState([])
    const [chatContent, setChatContent] = useState([])
    const [sessionId, setSessionId] = useState(0);
    const [chatInput, setChatInput] = useState("")

    const socket = socketIOClient(ENDPOINT);

    useEffect(() => {
        socket.on('connect', function (data) {
            socket.emit('admin-join', 'admin joining...')
        })
        socket.on('send-all-chat', (data)=>{
            setAllChatData(data)
        })
        // socket.on('messageSend-thread', (data) => {
        //     alert(data)
        // })
        socket.on("client-msg", function(data) {
            console.log(data)
        })
    },[])

    const handleOnChange = (event) => {
        setChatInput(event.target.value)
    }

    const sendChatInput = () => {
        event.preventDefault();
        setOpenChatContent(true)
        if (!sessionStorage.getItem('chat-id')) {
            sessionStorage.setItem('chat-id', Math.floor(Math.random() * 190000000) + 100000000);
        }
        socket.emit('firstMessage', {
            sessionId: sessionStorage.getItem('chat-id'),
            chatName: inputValue.chatName,
            chatEmail: inputValue.chatEmail,
            chatContent: [
                {
                    text: inputValue.chatContent,
                    time: new Date()
                }
            ]
        });
        axios.post('http://localhost:4000/chat', {
            sessionId: sessionStorage.getItem('chat-id'),
            chatName: inputValue.chatName,
            chatEmail: inputValue.chatEmail,
            chatContent: [
                {
                    text: inputValue.chatContent,
                    time: new Date()
                }
            ]}
        )
    }

    return (
        <div className="boxchat-admin flex">
            <div className="boxchat-list">
                <div className="boxchat-search">
                    <input type="text"></input>
                </div>
                { allChatData.length > 0 && 
                    allChatData.map((item,index)=>{
                        return (
                            <div 
                                key={index} 
                                className="boxchat-item flex-col"
                                onClick={()=>{setChatContent(item.chatContent)}}
                            >
                                {item.chatName}
                                {item.chatContent[0].text}
                            </div>
                        )
                    })
                }
            </div>
            <div className="boxchat-main">
                <div className="boxchat-box">
                    {
                        chatContent.map((item, index)=>{
                            return(
                                <div>
                                    {item.text}
                                </div>
                            )
                        })
                    }
                </div>
                <div className="boxchat-type">
                    <form onSubmit={sendChatInput}>
                        <input type="text" onChange={handleOnChange} name="chatInput"></input>
                        <button>Send</button>
                    </form>
                </div>
            </div>
        </div>
        // allChatData.map((item, index)=> {
        //     return (
        //         <div 
        //             key={index} className="flex"
        //             onClick={()=>{
        //                 setSessionId(item.sessionId)
        //                 console.log(item.sessionId)
        //                 socket.on('connect', function (data) {
        //                     socket.emit('sendRoomId', sessionId);
        //                 })
        //             }}
        //         >
        //             <p>{item.chatName}</p>
        //             <p>{item.chatEmail}</p>
        //             {/* {item.chatContent.map((item,index)=>{
        //                 return (
        //                     <div key={index}>
        //                         <p>{item.text}</p>
        //                         <p>{item.date}</p>
        //                     </div>
        //                 )
        //             })} */}
        //         </div>
        //     )
        // })
    )
}
