import React, { useState } from 'react'

export const UserContext = React.createContext();

export function UserProvider(props) {

    const [userInfo, setCartItems] = useState([]);

    return (
        <UserContext.Provider
            value={{
                userInfo: userInfo,
            }}
        >
            {props.children}
        </UserContext.Provider>
    )
}
