import React, { Component } from 'react';
import '../App.css';
import ClassNames from 'classnames'
class Banner extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentBanner: 1
        };
    }

    getNextBanner(banner) {
        switch (banner) {
            case 1:
                return 2;
            case 2:
                return 3;
            case 3:
                return 1; 
        }
    }

    componentDidMount() {
        setInterval(() => {
            console.log(this.state.currentBanner);
            this.setState({
                currentBanner: this.getNextBanner(this.state.currentBanner)
            })
        }, 1000);
    }

    render() {
        return(
            <div 
                className={ClassNames}
                className="Banner Banner-first">

            </div>
        )
    }
}
export default Banner;
