import React, { useContext, useState } from 'react';
import '../../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCartPlus, faCircle, faEye, faHeart } from "@fortawesome/free-solid-svg-icons";

import { CartContext } from '../../contexts/Cart'
import 'react-toastify/dist/ReactToastify.css';
import loadingGif from '../../assets/loading.gif'

function ProductOverlay(props) {

    const [loading, setLoading] = useState(0)

    const { 
        addToCart,
        addToWishList
    } = useContext(CartContext);

    const cartClick = () => {
        setLoading(1)
        setTimeout(()=>{
            setLoading(0)
            addToCart(props.product)
        }, 1000)
    }

    const wishListClick = () => {
        setLoading(2)
        setTimeout(()=>{
            setLoading(0)
            addToWishList(props.product)
        }, 1000)
    }

    return (
        <div 
            className="product-overlay">
            <div className="product-icon-box flex-center icon-cart btn"
                onClick={cartClick}
            >
                { loading === 1 && 
                    <div className="tab-loadmore-btn tab-loadmore-loading btn-nothover">
                        <FontAwesomeIcon icon={faCircle} className="loading-icon" style={{animationDelay: `0s`}}></FontAwesomeIcon>
                        <FontAwesomeIcon icon={faCircle} className="loading-icon" style={{animationDelay: `.2s`}}></FontAwesomeIcon>
                        <FontAwesomeIcon icon={faCircle} className="loading-icon" style={{animationDelay: `.4s`}}></FontAwesomeIcon>
                    </div>
                }
                { loading !== 1 &&  <FontAwesomeIcon icon={faCartPlus} style={{marginRight: '3px'}}/> }
            </div>
            <div className="product-icon-box flex-center icon-wishlist btn"
                onClick={wishListClick}
            >
                { loading === 2 && <div>...</div>}
                { loading !== 2 && <FontAwesomeIcon icon={faHeart}/>}
            </div>
            <div
                className="product-icon-box flex-center icon-view btn"
                onClick={props.openView}
                >
                <FontAwesomeIcon icon={faEye}/>
            </div>
        </div>
    )
}
export default ProductOverlay;
