import React, { Component } from 'react';
import '../App.css';
import Product from './Product.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Container,
    Row,
    Col
} from "reactstrap";

export default class BestSeller extends Component {

    constructor(props) {
        super(props);

        this.state = {
            product: [
                {
                    imgUrl: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/1_1-4-433x516.jpg",
                    imgUrlHover: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/2_1-4-433x516.jpg",
                    productTitle: "Hooded Coat",
                    productPrice: 200
                },
                {
                    imgUrl: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/1_1-4-433x516.jpg",
                    imgUrlHover: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/2_1-4-433x516.jpg",
                    productTitle: "Hooded Coat",
                    productPrice: 200
                },
                {
                    imgUrl: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/1_1-4-433x516.jpg",
                    imgUrlHover: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/2_1-4-433x516.jpg",
                    productTitle: "Hooded Coat",
                    productPrice: 200
                },
                {
                    imgUrl: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/1_1-4-433x516.jpg",
                    imgUrlHover: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/2_1-4-433x516.jpg",
                    productTitle: "Hooded Coat",
                    productPrice: 200
                },
                {
                    imgUrl: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/1_1-4-433x516.jpg",
                    imgUrlHover: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/2_1-4-433x516.jpg",
                    productTitle: "Hooded Coat",
                    productPrice: 200
                },
                {
                    imgUrl: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/1_1-4-433x516.jpg",
                    imgUrlHover: "https://demo.uix.store/sober/wp-content/uploads/sites/2/2016/07/2_1-4-433x516.jpg",
                    productTitle: "Hooded Coat",
                    productPrice: 200
                },
            ]
        }
    }

    render() {
        const { product } = this.state;
        return(
            <div className="BestSeller">
                <Container className="no-p">
                    <Row>
                        {product.map(function(item) {
                            return (
                                <Col>
                                    <Product 
                                        imgUrl={item.imgUrl} 
                                        imgUrlHover={item.imgUrlHover} 
                                        productTitle={item.productTitle}
                                        productTitle={item.productPrice}
                                    />
                                </Col>
                            )
                        })}
                    </Row>
                </Container>
            </div>
        )
    }
}
