import React, { useEffect, useState } from 'react'
import '../../../App.css'
import '../../../Styles/Dashboard.css'
import { faFileInvoice, faMoneyBillWave, faStar, faTshirt, faUser } from '@fortawesome/free-solid-svg-icons'
import DashboardTotalCount from './DashboardTotalCount'
import DashboardLocation from './DashboardLocation'
import DashboardTopFive from './DashboardTopFive'
import DashboardRecentReview from './DashboardRecentReview'
import axios from 'axios'

export default function DashboardMain() {

    const totalCount = [
        {
            id: 1,
            title: "Total orders",
            count: "3200",
            percent: 12,
            color: "orange",
            icon: faFileInvoice
        },
        {
            id: 2,
            title: "Total sales",
            count: "$120 000",
            percent: 20,
            color: "pink",
            icon: faTshirt
        },
        {
            id: 3,
            title: "Income",
            count: "$30 000",
            percent: 30,
            color: "green",
            icon: faMoneyBillWave
        },
        {
            id: 4,
            title: "Customers",
            count: "1200",
            percent: 5,
            color: "lightblue",
            icon: faUser
        },
    ]
    const [products, setProducts] = useState([]);

    useEffect(()=>{
        axios.get(`http://localhost:4000/products`)
            .then(res => {
                setProducts(res.data)
            }
        )
    }, [])

    // console.log(products);
    const recentVote = [];
    for (let i = 0; i < products.length; i++) {
            // id: products[i]._id,
            // productName: products[i].productName,
            const productVote = products[i].productVote;
            for (let j = 0; j < productVote.length; j++) {
                console.log(productVote[j])
                obj.key3 = "value3";
            }
    }

    console.log(recentVote)

    return (
        <div className="dashboard-main">
            <div className="row flex">
                { totalCount.map((item, index)=> {
                    return (
                        <DashboardTotalCount
                            key = {index}
                            icon = {item.icon}
                            title = {item.title}
                            count = {item.count}
                            percent = {item.percent}
                            color = {item.color}
                        />
                    )
                })}
            </div>
            <DashboardLocation/>
            <div className="row flex">
                <DashboardTopFive
                    icon = {faUser}
                    title = "Top customers"
                    color = "lightblue"
                />
                <DashboardTopFive
                    icon = {faTshirt}
                    title = "Top selling products"
                    color = "pink"
                />
            </div>
            <div className="row flex">
                <DashboardRecentReview
                    icon = {faStar}
                    title = "Customer Review"
                    color = "orange"
                    recentVote = {recentVote}
                />
            </div>
        </div>
    )
}
