import React, { useEffect, useState } from 'react';
import '../Styles/LoadingPage.css'
export default function LoadingPage(props) {

    const [textIndex, setTextIndex] = useState(1)

    useEffect(()=>{
        setTimeout(()=>{
            setTextIndex(2)
        }, 2000)
    },[])

    return(
        <div className="loading-page">
            <div>
                <p className={textIndex === 1 ? "text hello show" : "text hello"}>Hello!</p>
                <p className={textIndex === 2 ? "text welcome show" : "text welcome"}>Welcome to SOBER shop</p>
            </div>
        </div>
    )
}
