import React, { useEffect, useRef, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'

export default function DashboardSubscriberCreate(props) {

    const createForm = useRef();

    const [subscriberEmail, setSubscriberEmail] = useState("") 

    const onSubmit = (event) => {
        event.preventDefault()
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        const formData = new FormData();

        formData.append("subscriber", subscriberEmail);

        console.log(subscriberEmail)
        axios.post('http://pe.heromc.net:4000/email', formData, config)
        props.setCloseCreateFunc(false);
        props.setToastFunc(true);
    }

    return (
        <div className="DashboardProductInfo">
            <div className="create-box"> 
                <div className="create-box-title flex">
                    <div className="create-box-title-text">
                        Subcriber infomation
                    </div>
                    <div  
                        className="create-box-title-close flex-center"
                        onClick={()=>{
                            props.setCloseCreateFunc(false);
                        }}
                    >
                        <FontAwesomeIcon icon={faTimes}/>
                    </div>
                </div>
                <form onSubmit={onSubmit} encType="multipart/form-data" ref={createForm}>
                    <div className="create-box-row flex">
                        <div className="dashboard-left flex">Email</div>
                        <div className="dashboard-right">
                            <input 
                                type="text" name="email" 
                                value={subscriberEmail || ""}
                                onChange={(event)=>{
                                    setSubscriberEmail(event.target.value)
                                }} required
                                ></input>
                        </div>
                    </div>
                    <div className="flex-center" style={{marginTop: '40px'}}>
                        <button className="create-box-btn btn">
                            Create subcriber
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
}
