import React from 'reacr'

export default function DashboardInbox(props) {
    const [chatData, setChatData] = useState([])

    useEffect(() => {
        Axios.get(`http://localhost:4000/chat`)
            .then(res => {
                setChatData(res.data)
            }
        )
    },[])
    return (
        chatData.map((item, index)=> {
            return (
                <div key={index}>
                    <p>{item.chatName}</p>
                    <p>{item.chatEmail}</p>
                    {item.chatContent.map((item,index)=>{
                        return (
                            <div key={index}>
                                <p>{item.text}</p>
                                <p>{item.date}</p>
                            </div>
                        )
                    })}
                </div>
            )
        })
    )
}
