import React, { useEffect, useState } from 'react';
import '../../App.css';
import Product from '../Product.js';
import axios from 'axios'

export default function NewProducts() {

    const [products, setProducts] = useState([]);
    const [limit, setLimit] = useState(10);

    useEffect(() => {
        axios.get(`http://localhost:4000/products`)
            .then(res => {
                setProducts(res.data)
            }
        )
    },[])

    const handleClick = () => {
        console.log("ZXC")
    }


    let height = 750;
    //Sort by product sold
    if (products.length) {
        products.reverse();
        if (products.length <= 5) {
            height = 360;
        }
    }
    //Limit products
    products.slice(0, limit);
    console.log(products)

    return(
        <div>
            <div className="NewProducts" style={{ minHeight: `${height}px`}}>
                {products.map(function(item, index) {
                    return (
                        <Product 
                            key={index}
                            imgUrl={item.imgUrl} 
                            imgUrlHover={item.imgUrlHover} 
                            productTitle={item.productTitle}
                            productPrice={item.productPrice}
                            productSale={item.productSale}
                            productSold={item.productSold}
                        />
                    )
                })}
            </div>
            {products.length > 10 && 
                <div className="tab-loadmore flex-center">
                    <div className="tab-loadmore-btn flex-center">
                        Load More
                    </div>
                </div>
            }
        </div>
    )
}
