import React, { useContext, useEffect } from 'react'
import '../Styles/Chat.css'
import '../App.css'
import { withRouter } from 'react-router-dom'
import { ChatContext } from '../contexts/Chat'
import Axios from 'axios'

function ChatData(props) {
    const {
        chatData,
        setChatDataFunc
    } = useContext(ChatContext)

    useEffect(() => { 
        socket.on('thread', (data)=> {
            setChatData(chatData=>[...chatData, data]);
        })
        // Axios.get(`http://localhost:4000/chat/${sessionStorage.getItem('chat-id')}`)
        //     .then(res => {
        //         setChatDataFunc(Object.assign(res.data, chatData))
        //     }
        // )
    }, [])

    if (chatData.length > 0) console.log(chatData[0].chatName)

    return (
        <ul>
            <div className="chat-box-body">
                <form  className="form-chat hide_chat_box_item">
                    {chatData.length > 0 && <label>{chatData[0].chatName}</label> }
                </form>
            </div>
        </ul>
    )
}

export default withRouter(ChatData);
