import React from 'react'
import classNames from 'classnames'
import {
    Link
  } from "react-router-dom"; 
import Dropdown from './Dropdown';

export default function MenuItemDropdown(props) {
    const url = props.url;
    return (
        <div>
            <Link to={url}
                className={classNames({
                    active: props.location === {url},
                    whitelink_header: props.whiteText === true,
                })}
                id={props.id}
            >{props.nameItem}</Link>
            {props.dropdownHover === true && 
                <Dropdown 
                    className="dropdown-display"
                    text='chizxczcxzcxzxcm to'
                /> 
            }
        </div>
    )
}
