import React, { useEffect, useState } from 'react';
import '../../App.css';
import dropDownImg from '../../assets/women-dropdown.jpg'
import dropDownImg2 from '../../assets/women-dropdown2.jpg'

export default function Dropdown(props) {

    const [imgIndex, setImgIndex] = useState(0);
    const imgs = [
        dropDownImg,
        dropDownImg2,
    ]

    useEffect(()=>{
        setInterval(() => {
            setImgIndex(imgIndex + 1)
        }, 1000);
    })

    return(
        <div className="DropdownSlider">
            <div className="dropdownslider-container flex" style={{width: `${props.width}`, height: `${props.height}` }}>
                {
                    imgs.map((item, index) => {
                        return (
                            <img 
                                style={{transform: `translate(${width * imgIndex})`}}
                                src={item} width='100%' height='100%'></img>
                        )
                    })
                }
            </div>
        </div>
    )
}
