import React, { useState } from 'react';
import '../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCartPlus, faHeart } from "@fortawesome/free-solid-svg-icons";

export default function Product(props) {

    const [url, setUrl] = useState(this.props.imgUrl);

    return(
        <div className="Product"
            onMouseOver={()=> {setUrl(props.imgUrlHover)}}
            onMouseOut={()=> {setUrl(props.imgUrlHover)}}
        >
            <div className="product-img">
                <div className="img" style={{backgroundImage: `url(${url})`}}>
                    <img src={url} className="img" alt=""></img>
                </div>
                <div className="product-overlay">
                    <div className="product-cart product-icon">
                        <FontAwesomeIcon icon={faCartPlus} className="cart-icon cart-icon-right"/>
                    </div>
                    <div className="product-wishlist product-icon">
                        <FontAwesomeIcon icon={faHeart} className="cart-icon"/>
                    </div>
                </div>
            </div>
            <div className="product-title">
                {props.productTitle}
            </div>
            <div className="product-price">
                {props.productPrice} VNƒê
            </div>
        </div>
    )
}
