import React, { useEffect, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'

export default function DashboardProductInfo(props) {
    const [isCheckedSmall, setIsCheckedSmall] = useState(false);
    const [isCheckedMedium, setIsCheckedMedium] = useState(false);
    const [isCheckedLarge, setIsCheckedLarge] = useState(false);
    const [inputValue, setInputValue] = useState([])
    const [cate, setCate] = useState()

    const checkedSize = (event) => {
        if (event.target.id === "1") {
            if (isCheckedSmall) {
                setIsCheckedSmall(false)
            } else {
                setIsCheckedSmall(true)
            }
        }
        if (event.target.id === "2") {
            if (isCheckedMedium) {
                setIsCheckedMedium(false)
            } else {
                setIsCheckedMedium(true)
            }
        }
        if (event.target.id === "3") {
            if (isCheckedLarge) {
                setIsCheckedLarge(false)
            } else {
                setIsCheckedLarge(true)
            }
        }
    }

    const handleOnChange = (event) => {
        setInputValue({...inputValue, [event.target.name]: event.target.value})
    }

    useEffect(()=> {
        axios.get(`http://localhost:4000/category`)
            .then(res => {
                setCate(res.data)
            }
        )
    },[])

    const onSubmit = (event) => {
        event.preventDefault()
        axios.post('http://localhost:4000/products', {
            productName: inputValue.name,
            productSale: inputValue.sale,
            productPrice: inputValue.price,
            productCate: inputValue.cate,
            // productSize: inputValue.size,
            // productSex: inputValue.sex,
            // productSold: inputValue.sold,
            // productSale: inputValue.sale,
            // productImg: inputValue.image,
            // productVote: [],
            // productDate: new Date()
        })
        .then(res => {
            console.log(res.data)
        })
        .catch(err => {
            console.log(err.response.data);
        })
    }

    return (
        <div className="DashboardProductInfo">
            <div className="create-box">
                <div className="create-box-title flex">
                    <div className="create-box-title-text">
                        Product infomation
                    </div>
                    <div 
                        className="create-box-title-close flex-center"
                        onClick={()=>{
                            props.setCloseCreateFunc(false);
                        }}
                    >
                        <FontAwesomeIcon icon={faTimes}/>
                    </div>
                </div>
                <form onSubmit={onSubmit}>
                    <div className="create-box-row flex">
                        <div className="left flex">Name</div>
                        <div className="right">
                            <input type="text" name="name" onChange={handleOnChange} required></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="left flex">Images </div>
                        <div className="right">
                            <input type="file" name="image" className="noborder" onChange={handleOnChange}></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="left flex">Defaut price </div>
                        <div className="right">
                            <input type="number" name="price" placeholder="USD" onChange={handleOnChange}></input>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="left flex">Sale </div>
                        <div className="right flex-center">
                            <input type="number" placeholder="%" style={{ width: "100px"}} onChange={handleOnChange} name="sale"></input>
                            <label>From: </label>
                            <input type="date"  name="fromdate" onChange={handleOnChange} placeholder="dd/mm/yyyy" pattern="(^(((0[1-9]|1[0-9]|2[0-8])[\/](0[1-9]|1[012]))|((29|30|31)[\/](0[13578]|1[02]))|((29|30)[\/](0[4,6,9]|11)))[\/](19|[2-9][0-9])\d\d$)|(^29[\/]02[\/](19|[2-9][0-9])(00|04|08|12|16|20|24|28|32|36|40|44|48|52|56|60|64|68|72|76|80|84|88|92|96)$)"/>
                            <label>To: </label>
                            <input type="date"  name="todate" onChange={handleOnChange} placeholder="dd/mm/yyyy" pattern="(^(((0[1-9]|1[0-9]|2[0-8])[\/](0[1-9]|1[012]))|((29|30|31)[\/](0[13578]|1[02]))|((29|30)[\/](0[4,6,9]|11)))[\/](19|[2-9][0-9])\d\d$)|(^29[\/]02[\/](19|[2-9][0-9])(00|04|08|12|16|20|24|28|32|36|40|44|48|52|56|60|64|68|72|76|80|84|88|92|96)$)"/>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="left flex">Category </div>
                        <div className="right flex-center">
                            <select style={{ width: "350px"}}>
                                { cate &&
                                    cate.map((item, index) => {
                                        return(
                                            <option key={index}>{item}</option>
                                        )
                                    })
                                }
                            </select>
                            <input type="text" name="cate" placeholder="New category?" style={{  margin:'0 10px'}} onChange={handleOnChange}></input>
                            <div className="btn" style={{
                                fontSize: '14px',
                                fontFamily: 'sans-serif',
                                fontWeight: '300',
                                padding: '0 10px',
                                cursor: 'pointer',
                                width: '350px',
                                height: '30px'
                            }}>
                                Add new category
                            </div>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="left flex">Size </div>
                        <div className="right flex">
                            <div 
                                className={isCheckedSmall ? "size-check isChecked" : "size-check"}
                                id="1" 
                                onClick={checkedSize}>Small</div>
                            <div 
                                className={isCheckedMedium ? "size-check isChecked" : "size-check"}
                                id="2" 
                                onClick={checkedSize}>Medium</div>
                            <div 
                                className={isCheckedLarge ? "size-check isChecked" : "size-check"}
                                id="3" 
                                onClick={checkedSize}>Large</div>
                        </div>
                    </div>
                    <div className="create-box-row flex">
                        <div className="left flex">Description </div>
                        <div className="right">
                            <input type="text"></input>
                        </div>
                    </div>

                    <div className="flex-center" style={{marginTop: '40px'}}>
                    <button className="create-box-btn btn">
                        Add product
                    </button>
                </div>
                </form>
            </div>
        </div>
    )
}
