    //         }
    //         if(window.pageYOffset < 100) {
    //             if (this.state.location === "/news") {
    //                 this.setState({
    //                     scrolled: false,
    //                     isWhiteText: true
    //                 })
    //             } else {
    //                 this.setState({
    //                     scrolled: false,
    //                     isWhiteText: false
    //                 })
    //             }
    //         } else if (this.prev > window.pageYOffset) {
    //             if (this.state.location === "/news") {
    //                 this.setState({
    //                     scrolled: false,
    //                     isWhiteBox: true,
    //                     isWhiteText: false
    //                 })
    //             } else {
    //                 this.setState({
    //                     scrolled: false,
    //                     isWhiteBox: true
    //                 })
    //             }
    //         }else {
    //             this.setState({
    //                 scrolled: true
    //             })
    //         }
    //         this.prev = window.pageYOffset;
    //     }.bind(this);
    // }

    componentDidMount() {
        window.onscroll = function() {

            if (this.state.location === "/news") {
                if(window.pageYOffset === 0) {
                    this.setState({
                        isWhiteBox: false,
                        isWhiteText: true
                    })
                } else if(window.pageYOffset < 100) {
                    this.setState({
                        scrolled: false,
                        isWhiteText: true
                    })
                } else if (this.prev > window.pageYOffset) {
                        this.setState({
                            scrolled: false,
                            isWhiteBox: true
                        })
                } else {
                    this.setState({
                        scrolled: true
                    })
                }
            } else {
                if(window.pageYOffset === 0) {
                    this.setState({
                        isWhiteBox: false
                    })
                } else if(window.pageYOffset < 100) {
                    this.setState({
                        scrolled: false
                    })
                } else if (this.prev > window.pageYOffset) {
                        this.setState({
                            scrolled: false,
                            isWhiteBox: true
                        })
                } else {
                    this.setState({
                        scrolled: true
                    })
                }
            }
            // if(window.pageYOffset < 100) {
            //     if (this.state.location === "/news") {
            //         this.setState({
            //             scrolled: false,
            //             isWhiteText: true
            //         })
            //     } else {
            //         this.setState({
            //             scrolled: false,
            //             isWhiteText: false
            //         })
            //     }
            // } else if (this.prev > window.pageYOffset) {
