import React, { useEffect, useState } from 'react';
import '../../App.css';
import BestSeller from "./BestSeller.js"
import NewProducts from "./NewProducts.js"
import SalesProducts from "./SalesProducts.js"
import axios from 'axios'

export default function HomeTab() {
    const [currentTab, setCurrentTab] = useState(1)
    const [isActive, setIsActive] = useState(1)
    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:4000/products`)
            .then(res => {
                setProducts(res.data)
            }
        )
    },[])

    //Get product sold
    let height = 750;
    let soldProduct = [];
    if (products.length) {
        soldProduct = products.sort((a,b) =>  b.productSold - a.productSold)
        if (soldProduct.length <= 5) {
            height = 360;
        }
    }


    //Get product selling
    const sellingProduct = [];
    for (let i = 0; i < products.length; i++) {
        if (Number(products[i].productSale) > 0) {
            sellingProduct.push(products[i]);
        }
    }
    if (sellingProduct.length) {
        if (sellingProduct.length <= 5) {
            height = 360;
        }
    }

    return(
        <div className="HomeTab">
            <div className="home-tab flex-center">
                <p onClick={() => {setCurrentTab(1); setIsActive(1)}} className={isActive === 1 ? "home-tab-active" : ""}>Best Sellers</p>
                <p onClick={() => {setCurrentTab(2); setIsActive(2)}} className={isActive === 2 ? "home-tab-active" : ""}>New Products</p>
                <p onClick={() => {setCurrentTab(3); setIsActive(3)}} className={isActive === 3 ? "home-tab-active" : ""}>Sales Products</p>
            </div>
            <div className="tab-content">
                { // best seller
                    currentTab === 1 && 
                    <BestSeller 
                        products={soldProduct}
                        height={height}
                    />
                }
                { // new product
                    currentTab === 2 && 
                    <BestSeller 
                        products={products}
                        height={height}
                    />
                }
                { // sale product
                    currentTab === 3 && 
                    <BestSeller 
                        products={sellingProduct}
                        height={height}
                    />
                }
            </div>
        </div>
    )
}
