import React, { useEffect, useState } from 'react'

export const CartContext = React.createContext();

export function CartProvider(props) {

    const [cartItems, setCartItems] = useState([]);
    const [clickedCart, setClickedCart] = useState(0);

    const test = (productId) => {
        console.log(productId)
        for (let i of cartItems) {
            if (i.id === productId) {
                i.count = i.count + 1
            }
        }

        cartItems.filter((item)=>{
            // if (item.id === productId && item.count === 1)
            return item.id === productId && item.count === 1
        })
    }

    const addToCart = (product) => {
        setClickedCart(clickedCart + 1) // scroll on click to cart

        test(product._id)
        setCartItems(cartItems=>[...cartItems, {id: product._id, count: 1}])
    }

    console.log(cartItems)

    return (
        <CartContext.Provider
            value={{
                cartItems: cartItems,
                addToCart: addToCart,
                clickedCart: clickedCart
            }}
        >
            {props.children}
        </CartContext.Provider>
    )
}
