import React, { useEffect, useState } from 'react'
import '../../../../App.css'
import '../../../../Styles/Dashboard.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faGlobe } from '@fortawesome/free-solid-svg-icons' 
import axios from 'axios' 
import { Bar } from '@reactchartjs/react-chart.js'

export default function DashboardChart(props) {

    const order = props.order
    const [productList, setProductList] = useState([])

    const data = { 
        labels: ['Jan', 'Feb', 'Mar', 'Apr', 'Mai', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
        datasets: [
            {
                label: '# of Red Votes',
                data: [12, 19, 3, 5, 2, 3],
                backgroundColor: 'rgb(255, 99, 132)',
            },
            {
                label: '# of Blue Votes',
                data: [2, 3, 20, 5, 1, 4],
                backgroundColor: 'rgb(54, 162, 235)',
            },
            {
                label: '# of Green Votes',
                data: [3, 10, 13, 15, 22, 30],
                backgroundColor: 'rgb(75, 192, 192)',
            },
        ],
    }  
    const options = {
        scales: {
            yAxes: [
            {
                ticks: {
                    beginAtZero: true,
                },
            },
            ],
        },
    }

    useEffect(()=>{
        if (order.length > 0) { 
            const arr = []
            for (let i in order) {
                for (let j in order[i].orderList) {
                    arr.push(order[i].orderList[j])
                }
            } 
            arr.map((item)=>{ 
                axios.get(`http://pe.heromc.net:4000/products/${item.id}`)
                    .then(res => { 
                        setProductList(productList=>[...productList, res.data])
                    })
            })
        } 
    }, [props.order]) 


    if (productList.length > 0) { 
        var result = [];
        console.log(productList)
        productList.forEach(function(obj) {
            var id = obj._id
            if(!this[id]) 
                result.push(this[id] = obj);
            else 
                this[id].amount += obj.amount;
        }, Object.create(null));
        console.log(result)
    }

    return (
        <div className="chart flex-col">
            <div className="headerbox flex-center">
                <FontAwesomeIcon icon={faGlobe} className="icon"/>
            </div>
            <div className="top-location-container">
                <div className="headerbox-header">
                    <p>2020 Sales by Top 3 Categories</p>
                </div>
                <div className="top-location-content flex">
                    <div className="top-location-map" style={{margin: '0'}}>
                        <Bar data={data} options={options} /> 
                    </div>
                </div>
            </div>
        </div>
    )
}
