import React, { useEffect, useState } from 'react';
import '../../App.css';
import BestSeller from "./BestSeller.js"
import axios from 'axios'

export default function HomeTab() {
    const [currentTab, setCurrentTab] = useState(1)
    const [isActive, setIsActive] = useState(1)
    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:4000/products`)
            .then(res => {
                setProducts(res.data)
            }
        )
    },[])

    //Get product sold
    let height = 750;
    if (products.length) {
        products.sort((a,b) =>  b.productSold - a.productSold);
        if (products.length <= 5) {
            height = 360;
        }
    }

    if (dateProduct) {
        dateProduct.sort(function(a,b){
            return new Date(b.productDate) - new Date(a.productDate);
        });
    }

    // Get product selling
    const sellingProduct2 = []
    if (sellingProduct.length) {
        for (let i = 0; i < sellingProduct.length; i++) {
            if (Number(sellingProduct[i].productSale) > 0) {
                sellingProduct2.push(sellingProduct[i]);
            }
        }
        if (sellingProduct.length <= 5) {
            height = 360;
        }
    }

    return(
        <div className="HomeTab">
            <div className="home-tab flex-center">
                <p onClick={() => {setCurrentTab(1); setIsActive(1)}} className={isActive === 1 ? "home-tab-active" : ""}>Best Sellers</p>
                <p onClick={() => {setCurrentTab(2); setIsActive(2)}} className={isActive === 2 ? "home-tab-active" : ""}>New Products</p>
                <p onClick={() => {setCurrentTab(3); setIsActive(3)}} className={isActive === 3 ? "home-tab-active" : ""}>Sales Products</p>
            </div>
            <div className="tab-content">
                { // best seller
                    currentTab === 1 && 
                    <BestSeller 
                        products={products}
                        height={height}
                    />
                }
                { // new product
                    currentTab === 2 && 
                    <BestSeller 
                        products={products}
                        height={height}
                    />
                }
                { // sale product
                    currentTab === 3 && 
                    <BestSeller 
                        products={products}
                        height={height}
                    />
                }
            </div>
        </div>
    )
}
