import React, { Component } from 'react';
import '../App.css';
import {
    Link,
    withRouter
  } from "react-router-dom"; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import classNames from 'classnames';
import {  faSearch, faUser, faCartPlus } from "@fortawesome/free-solid-svg-icons";

class Header extends Component {
    constructor(props) {
        super(props);

        this.state = {
            scrolled: false,
            isWhite: false,
            activeId: 1,
        }
        this.StringToSlug = this.StringToSlug.bind(this)
    }

    componentDidMount() {
        window.addEventListener('scroll', e => this.handleNavigation(e));
    }

    handleNavigation = (e) => {
        const window = e.currentTarget;

        const isTop = window.scrollY < 100;
        if (window.scrollY  === 0) {
            this.setState({
                isWhite: false
            })
        }

        if (isTop !== true) {
            this.setState({
                scrolled: true,
                isWhite: true
            })
        } else {
            this.setState({
                scrolled: false
            })
        }

        if (this.prev > window.scrollY) {
            this.setState({
                scrolled: false
            })
        }
        this.prev = window.scrollY;
    };

    // componentWillUnmount() {
    //     window.removeEventListener('scroll');
    // }

    StringToSlug (str) {
        str = str.replace(/^\s+|\s+$/g, ''); // trim
        str = str.toLowerCase();

        // remove accents, swap ñ for n, etc
        var from = "àáäâèéëêìíïîòóöôùúüûñç·/_,:;";
        var to   = "aaaaeeeeiiiioooouuuunc------";
        for (var i=0, l=from.length ; i<l ; i++) {
            str = str.replace(new RegExp(from.charAt(i), 'g'), to.charAt(i));
        }

        str = str.replace(/[^a-z0-9 -]/g, '') // remove invalid chars
            .replace(/\s+/g, '-') // collapse whitespace and replace by -
            .replace(/-+/g, '-'); // collapse dashes

        return str;
    }

    render() {
        console.log(this.props.history.location);
        return(
            <div 
                className={classNames('Header', {
                    scrolled: this.state.scrolled === true,
                    white: this.state.isWhite === true
                })}
                >
                <ul className="menu flex-center">
                    <li>
                        <Link 
                            to="/" 
                            className={classNames({
                                active: this.state.activeId === 1
                            })}
                            id="1" 
                            >home</Link>
                    </li>
                    <li>
                        <Link to="/news"
                            className={classNames({
                                active: this.state.activeId === 2
                            })}
                            id="2" 
                            >News</Link>
                    </li>
                    <li>
                        <Link to="/men" 
                            className={classNames({
                                active: this.state.activeId === 3
                            })}
                            id="3"
                            >men</Link>
                    </li>
                    <li>
                        <Link to="/women" 
                            className={classNames({
                                active: this.state.activeId === 4
                            })}
                            id="4"
                            >women</Link>
                    </li>
                    <li>
                        <Link to="/children" 
                            className={classNames({
                                active: this.state.activeId === 5
                            })}
                            id="5"
                            >children</Link>
                    </li>
                </ul>
                <div className="logo flex-center">
                    <Link to="/">
                        <img src="https://demo.uix.store/sober/wp-content/themes/sober/images/logo.svg" alt="logo"></img>
                    </Link>
                </div>
                <div className="cart flex-center"> 
                    <FontAwesomeIcon icon={faSearch} className="icon"/>
                    <FontAwesomeIcon icon={faUser} className="icon"/>
                    <div className="icon flex-center">
                        <FontAwesomeIcon icon={faCartPlus} className="cart-icon"/>
                        <div className="cart-count">
                            <p>0</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default withRouter(Header);
