            </div> 
            <div className="newsbanner-breadcrumb flex-center">
                <div>Home</div>
                <FontAwesomeIcon icon={faAngleRight} className="cart-icon" style={{margin: '0 10px'}}/>
                <div style={{textTransform: 'capitalize'}}>{locationText}</div>
            </div>
        </div>
    )
}

