import React, { useRef } from 'react'
import '../../../../App.css'
import '../../../../Styles/Dashboard.css'
import { useEffect, useState } from 'react'
var parse = require('html-react-parser');


export default function DashboardEmail(props) {

    const [emailList, setEmailList] = useState([])
    useEffect(() => {
        for (let i in props.email) {
            if (props.email[i] !== false) {
                setEmailList(emailList=>[...emailList, props.email[i]])
            }
        }
    },[props.email])

    console.log(emailList)

    return (
        <div className="boxchat-admin ">
            { emailList.length > 0 &&
                <div 
                    className="flex-col chat-box-list">
                        {parse(props.email)}
                </div>  
            }
        </div>
    )
}
