import React, { useEffect, useState } from 'react'
import classNames from 'classnames'
import {
    Link, withRouter
  } from "react-router-dom"; 
import Dropdown from './Dropdown';

function MenuItemDropdown(props) {

    const [dropdownHover, setDropdownHover] = useState(false)
    const location = props.location.pathname;

    useEffect(()=>{
        function onScroll() {
            if(window.pageYOffset < 50) { // top
                setWhiteBox(false)
                setWhiteText(false)
            } else if (this.prev < window.pageYOffset) { //down
                setWhiteBox(true)
                setScrolled(true)
            } else if (this.prev > window.pageYOffset) { //up
                setScrolled(false)
            }
            this.prev = window.pageYOffset;
        }

        window.addEventListener("scroll", onScroll);
        return() => {
            window.removeEventListener("scroll", onScroll);
        }
    }, [location]);

    return (
        <li 
            className="menu-item"
            onMouseEnter={()=>{
                setDropdownHover(true)
            }}
            onMouseLeave={()=>{
                setDropdownHover(false)
            }}
            >
            <Link to={props.url}
                className={classNames({
                    active: location === props.url,
                    whitelink_header: props.whiteText === true,
                })}
            >{props.label}</Link>
            {(dropdownHover === true && props.dropdownContent.length > 0) && 
                <Dropdown 
                    className="dropdown-display"
                    dropdownContent={props.dropdownContent}
                    label={props.label}
                /> 
            }
        </li>
    )
}
export default withRouter(MenuItemDropdown);
