import React, { useState } from 'react';
import '../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes , faCheck } from '@fortawesome/free-solid-svg-icons';

export default function Account(props) {

    const [check, setCheck] = useState(false);

    return(
        <div className={props.accountOpen === false ? 'Account displayNone' : 'Account'}>
            <div className="search-header flex">
                <div className="search-title">My Account</div>
                <div
                    className="search-close"
                    onClick={props.clickToClose}
                    >
                    <FontAwesomeIcon 
                        icon={faTimes}
                        className="icon"
                        />
                </div>
            </div >
            <div className={props.accountOpen === false ? '' : 'fadeIn'}>
                <div 
                    className='search-tab login-tab flex'>
                    <div className="search-tab-cate search-tab-active">Login</div>
                    <div className="search-tab-cate">Register</div>
                </div>
                <div className="search-form login-form">
                    <form className="flex-col">
                        <input placeholder="Username"/>
                        <input placeholder="Password"/>
                        <div className="remember-login flex">
                            <div 
                                className="check-box" 
                                onClick={() => {
                                    if (check) {
                                        setCheck(false)
                                    } else {
                                        setCheck(true)
                                    }
                                }}>
                            </div>
                            {check && 
                                <div className="check-box-active flex-center">
                                    <FontAwesomeIcon className="check-box-active" icon={faCheck}></FontAwesomeIcon>
                                </div>
                            }
                            <p>Remember me</p>
                        </div>
                        <button>LOGIN</button>
                    </form>
                </div>
            </div>
        </div>
    )
}
