import React, { useContext, useEffect, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMinus, faPlus, faTimes  } from '@fortawesome/free-solid-svg-icons';
import { CartContext } from '../../contexts/Cart';

export default function CartItem(props) {

    const { cartItems, updateCart } = useContext(CartContext)
    const [cartCombine, setCartCombine] = useState([])

    useEffect(()=> {
        const virtualCart = Object.values(cartItems.reduce((item, {_id, productName, productPrice, productImg}) => {
            item[_id] = item[_id] || {_id, productName, productPrice, productImg, count: 0};
            item[_id].count++;
            return item;
        }, Object.create(null)));
        console.log(virtualCart)
        setCartCombine(virtualCart)
    },[cartItems])

    const removeFromCart = (event) => {
        const id = event.target.id
        const virtualCart = [...cartCombine]
        for (let i=0;i<virtualCart.length;i++) {
            if (virtualCart[i]._id === id)
                virtualCart.splice(i, 1)
        }
        setCartCombine(virtualCart)
        updateCart(virtualCart)
    }

    const minusCount = (event) => {
        const id = event.target.id
        const virtualCart = [...cartCombine]
        for (let i=0;i<virtualCart.length;i++) {
            if (virtualCart[i]._id === id) {
                if (virtualCart[i].count > 1) {
                    virtualCart[i].count = virtualCart[i].count - 1
                }
            }
        }
        setCartCombine(virtualCart)
    }
    const plusCount = (event) => {
        const id = event.target.id
        const virtualCart = [...cartCombine]
        for (let i=0;i<virtualCart.length;i++) {
            if (virtualCart[i]._id === id) {
                    virtualCart[i].count = virtualCart[i].count + 1
            }
        }
        setCartCombine(virtualCart)
    }

    return (
        <div className="search-form login-form fadeToRight" style={{width: '100%'}}>
            <div className="cart-list">
                {
                    cartCombine.length === 0 && 
                    <div>
                        No cart
                    </div>
                }
                {
                    cartCombine.map((item, index) => {
                        return (
                            <div className="cart-item flex">
                                <div className="cart-product-img">
                                    <img src={item.productImg[0]} width="80px" height="100%"></img>
                                </div>
                                <div className="cart-product-name">{item.productName}</div>
                                <div className="cart-product-amount flex-center">
                                    <div className="count-cart noselect">
                                        <div 
                                            className="count-cart-item left flex-center"
                                            id={item._id}
                                            onClick={minusCount}
                                            >
                                            <FontAwesomeIcon  style={{pointerEvents: 'none'}} icon={faMinus}/>
                                        </div> 
                                        <div className="count-cart-item text flex-center">
                                            <form style={{width: '100%', margin: '0', height: '30px'}}>
                                                <input 
                                                    style={{width: '100%', margin: '0', height: '30px'}}
                                                    type="text" 
                                                    value={item.count}
                                                    // onChange={e => setCountCart(Number(e.target.value))}
                                                />
                                            </form>
                                        </div>
                                        <div 
                                            className="count-cart-item right flex-center"
                                            id={item._id}
                                            onClick={plusCount}
                                            >
                                            <FontAwesomeIcon  style={{pointerEvents: 'none'}} icon={faPlus}/>
                                        </div>
                                    </div>
                                </div>
                                <div className="cart-product-price">{item.productPrice}</div>
                                <div className="cart-product-totalprice">{item.productPrice}</div>
                                <div className="cart-product-delete"
                                    onClick={removeFromCart}
                                    id={item._id}>
                                    <FontAwesomeIcon style={{pointerEvents: 'none'}} icon={faTimes}/>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}
